mapper.select:
select rownum, post_id, post_response, post_title, post_content, post_writedate, user_email
from (select * from posts where post_type='Q' and post_requestid is null)
order by rownum desc

select rownum, post_id, post_response, post_title, post_content, post_writedate, user_email
from (select * from posts where post_type='N' and post_requestid is null)
order by rownum desc

select rownum, post_id, post_response, post_title, post_content, post_writedate, user_email
from (select * from posts where post_type='F' and post_requestid is null)
order by rownum desc
